#if dataset is to big you may make a rectangular instead of square -- halve the columns number
distances = pd.DataFrame(index=indexes, columns=indexes[:len(indexes)//1000])
for k in indexes:
    for m in indexes:
        try:
            distances.loc[k][m] = contacts[ (contacts['chrom2_end_locus']//1000 == int(m[m.find('_') + 1:]))&(contacts['chrom2_name'] == m[:m.index('_')]) &(contacts['chrom1_name'] == k[:k.index('_')]) & (contacts['chrom1_end_locus']//1000 == int(k[k.find('_') + 1:]))]['distance_alpha=0.1'].iat[0]
            break
        except IndexError:
            distances.loc[k][m] = 2.0

CG = nx.Graph() # CG for ChromosomeGraph
for i in indexes:
    for k in indexes:
        if (i[:i.index('_')] == k[:k.index('_')]) and (int(i[i.index('_')+1:]) == (int(k[k.index('_')+1:])-1)):
            CG.add_edge('{}'.format(i),'{}'.format(k), weight = 0.01)
        elif distances.loc[i][k] != 2.0:
            CG.add_edge('{}'.format(i),'{}'.format(k), weight=distances.loc[i][k] )
        else:
            continue
